class Track {
  constructor() {
    this.points = [];
    this.color = hl.randomElement(lightColors);
  }

  addPoint(x, y) {
    this.points.push({ x, y });
  }

  show() {
    push();
    // opacity set to 60
    stroke(...this.color);
    strokeWeight(width / 20 + 2); // Increased line thickness for border
    noFill();
    beginShape();
    for (let point of this.points) {
      vertex(point.x, point.y);
    }
    endShape();
    pop();
  }
}

class Bird {
  constructor(x, y, isOpenScreen, speed, direction, openScreenType = "move") {
    this.x = x;
    this.y = y;
    this.r = width / 16;
    this.gravity = (height / (50 * 40)) * ((bodyweight / 10) * 1.5 + 1);
    this.velocity = 0;
    this.lift = (-height / 55) * (jump / 15 + 1);
    this.friction = 0.1;
    this.score = 0;
    this.up = false;
    this.particles = [];
    this.isOpenScreen = isOpenScreen;
    this.openScreenType = openScreenType;
    this.track = new Track();
    openScreenBirdTracks.push(this.track);
    this.speed = speed;
    this.direction = direction;
  }
  clear() {
    this.x = birdStartPosition.x;
    this.y = birdStartPosition.y;
    this.score = 0;
    this.up = false;
  }
  loadBirdComponent(img, r, g, b, scale = 1, offset = { x: 0, y: 0 }) {
    const openScreenScale = this.openScreenType === "static" ? 2 : 1;
    tint(r, g, b);
    image(
      img,
      offset.x,
      offset.y,
      this.r * openScreenScale * scale,
      this.r * 1.18 * openScreenScale * scale
    );
  }
  show(isRestart) {
    for (let particle of this.particles) {
      particle.show();
    }

    fill(255);
    push();
    imageMode(CENTER);
    translate(this.x, this.y);
    if (!isRestart) {
      if (this.up || this.velocity < 0) {
        rotate(-35);
      } else if (playing) {
        rotate(35);
      }
      if (this.isOpenScreen && this.direction) {
        switch (this.direction) {
          case "southeast":
            rotate(45);
            break;
          case "northeast":
            rotate(-45);
            break;
        }
      }
    }
    this.loadBirdComponent(birdSkin.beak[0], ...birdSkin.beak[1]);
    this.loadBirdComponent(birdSkin.body[0], ...birdColor, 0.983, {
      x: 0,
      y: 1.8,
    });
    this.loadBirdComponent(birdSkin.shadow[0], ...birdSkin.shadow[1], 0.984);
    this.loadBirdComponent(birdSkin.wing[0], ...birdSkin.wing[1]);
    this.loadBirdComponent(birdSkin.feather[0], ...birdSkin.feather[1]);
    this.loadBirdComponent(birdSkin.eyelid[0], ...birdSkin.eyelid[1]);
    this.loadBirdComponent(birdSkin.eyeline[0], ...birdSkin.eyeline[1]);
    this.loadBirdComponent(birdSkin.eye[0], ...birdSkin.eye[1]);
    pop();
  }

  update() {
    this.velocity += this.gravity;
    this.velocity = constrain(this.velocity, -25, 25);
    this.y += this.velocity;
    this.particles.push(new Particle(this.x, this.y, particleSpeed, birdColor));
    for (let i = this.particles.length - 1; i >= 0; i--) {
      this.particles[i].update();
      if (this.particles[i].isDone()) {
        this.particles.splice(i, 1);
      }
    }
    if (this.y > height) {
      this.velocity = 0;
      this.y = height;
    } else if (this.y < 0) {
      this.velocity = 0;
      this.y = 0;
    }
    this.up = false;
  }

  flap() {
    window.sounds.jumpSound.stop();
    window.sounds.jumpSound.play();
    this.velocity += this.lift;
    this.velocity *= 1 - this.friction;
    this.up = true;
  }

  move() {
    switch (this.direction) {
      case "southeast":
        this.x += this.speed;
        this.y += this.speed;
        break;
      case "southwest":
        this.x -= this.speed;
        this.y += this.speed;
        break;
      case "northeast":
        this.x += this.speed;
        this.y -= this.speed;
        break;
      case "northwest":
        this.x -= this.speed;
        this.y -= this.speed;
        break;
    }
    this.track.addPoint(this.x, this.y);
  }

  isOutOfScreen() {
    return this.x < 0 || this.y < 0 || this.x > width || this.y > height;
  }
}

class Particle {
  constructor(x, y, velocityX, color) {
    this.position = createVector(x, y);
    this.velocity = createVector(
      velocityX + hl.random(-1, 1),
      hl.random(-1, 1)
    );
    this.lifespan = 255;
    this.color = color;
  }

  update() {
    this.position.add(this.velocity);
    this.lifespan -= 6; // Adjust for faster or slower fade
  }

  show() {
    noStroke();
    fill(...this.color, this.lifespan); // Use bird's color or any color you like
    ellipse(this.position.x, this.position.y, width / 100); // Adjust size as needed
  }

  isDone() {
    return this.lifespan < 0;
  }
}

class Pipe {
  constructor() {
    this.x = width;
    this.w = width / 10;
    this.gap = height / hl.random(2.6, 3.4) - difficulty * 0.02 * height;
    this.min_height = height / 3.6;
    this.max_height = height - this.min_height - this.gap;
    this.top = floor(hl.random(this.min_height, this.max_height));
    this.speed = Math.floor(width / 150);
    this.isPass = false;
    this.isThin = hl.randomBool(0.5);
    this.flower = hl.randomElement(
      this.isThin ? pipeSkin.thin.flowers : pipeSkin.fat.flowers
    );
    this.bone = this.isThin ? pipeSkin.thin.bone : pipeSkin.fat.bone;
    this.boneColor = hl.randomElement(pipeImg.rgbs.bone).split(",");
    this.flowerColor = hl.randomElement(pipeImg.rgbs.flower).split(",");

    this.revIsThin = hl.randomBool(0.5);
    this.revFlower = hl.randomElement(
      this.revIsThin ? pipeRevSkin.thin.flowers : pipeRevSkin.fat.flowers
    );
    this.revBone = this.revIsThin
      ? pipeRevSkin.thin.bone
      : pipeRevSkin.fat.bone;
    this.revBoneColor = hl.randomElement(pipeImg.rgbs.bone).split(",");
    this.revFlowerColor = hl.randomElement(pipeImg.rgbs.flower).split(",");
  }

  show() {
    push();
    tint(...this.revBoneColor);
    image(this.revBone, this.x, 0, this.w, this.top);
    pop();
    push();
    tint(...this.revFlowerColor);
    image(this.revFlower, this.x, 0, this.w, this.top);
    pop();
    const heightB = height - this.gap - this.top;
    const yB = height - heightB;
    push();
    tint(...this.boneColor);
    image(this.bone, this.x, yB, this.w, heightB);
    pop();
    push();
    tint(...this.flowerColor);
    image(this.flower, this.x, yB, this.w, heightB);
    pop();
    // this.topPipe();
    // this.bottomPipe();
  }

  offScreen() {
    if (this.x + this.w + this.speed < 0) {
      return true;
    }
    return false;
  }

  offBird() {
    if (this.x + this.w + this.speed < bird.x) {
      return true;
    }
    return false;
  }

  hit(bird) {
    // hit screen bottom
    if (bird.y >= height) {
      return true;
    }
    if (bird.x > this.x && bird.x < this.x + this.w) {
      if (bird.y < this.top || bird.y > this.top + this.gap) {
        window.sounds.hitSound.play();
        return true;
      } 
    }
    return false;
  }

  pass(bird) {
    if (bird.x > this.x + this.w) {
      return true;
    }
    return false;
  }

  update() {
    this.x -= this.speed;
  }
}

