function setup() {
  canvasWidth = Math.min(windowWidth, windowHeight);
  let canvas = createCanvas(canvasWidth, canvasWidth);
  canvas.parent("cw");
  frameRate(40);
  initialProperties();
  angleMode(DEGREES);
  textStyle(BOLD);
  textSize(h1);
  // width * 0.25, height * 0.38, width * 0.5, height * 0.548
  restartButton = {
    x: width * 0.25,
    y: height * 0.38,
    w: width * 0.5,
    h: height * 0.548,
  };
  function isClickRestart() {
    // inside the button
    if (
      mouseX > restartButton.x &&
      mouseX < restartButton.x + restartButton.w &&
      mouseY > restartButton.y &&
      mouseY < restartButton.y + restartButton.h
    ) {
      return true;
    } else {
      return false;
    }
  }

function keyPressed$() {
  if (playing && key == " ") {
    bird.flap();
  } else if (!isRestart) {
    playing = true;
    clear$();
    bird.flap();
  }
}

function mousePressed$(event) {
  event.preventDefault();
  if (!playing && isRestart && isClickRestart()) {
    playing = true;
    clear$();
    bird.flap();
    return;
  }
  if (!playing && !isRestart) {
    playing = true;
    clear$();
    bird.flap();
    return;
  }
  // click inside the canvas
  if ( mouseX > 0 && mouseX < width && mouseY > 0 && mouseY < height) {
    bird.flap();
  }
}
  document.addEventListener("keypress", keyPressed$);
  document.addEventListener("mousedown", mousePressed$);
  // Prevent default touch actions for the entire document or specific elements
  document.addEventListener(
    "touchstart",
    function (e) {
      e.preventDefault();
    if (!playing && isRestart && isClickRestart()) {
      playing = true;
      clear$();
      bird.flap();
      return;
    }
    if (!playing && !isRestart) {
      playing = true;
      clear$();
      bird.flap();
      return;
    }
    // click inside the canvas
    if ( mouseX > 0 && mouseX < width && mouseY > 0 && mouseY < height) {
      bird.flap();
  }
    },
    { passive: false }
  );
  document.documentElement.style.touchAction = "manipulation";
  document.body.style.touchAction = "manipulation";
}

function clear$() {
  pipes = [];
  openScreenBird = null;
  openScreenBirdTracks = [];
  bird.particles = [];
  bird = new Bird(birdStandingPosition.x, birdStandingPosition.y);
  bird.clear();
}

function draw() {
  background(255, 255, 255);
  // bird.show();

  if (!playing) {
    if (isRestart) {
      image(endImg, restartButton.x, restartButton.y, restartButton.w, restartButton.h);
      fill(252, 146, 1);
      textSize(h2);
      text("Score: " + bird.score, width / 2, height / 1.3);
      fill(0);
      textSize(h3);
      text("Press to Restart", width / 2, height / 1.15);
      fill(0);
      noStroke();
      rect(0, height / 2, width / 2.1, height / 36);
      bird.x = endPosition.x;
      bird.y = endPosition.y;
      bird.particles = [];
      fill(216, 223, 229);
      rectMode(CENTER);
      noStroke();
      rect(width / 2, height / 5, width / 1.2, width / 4.5);
      textSize(h3);
      fill(0);
      const textX = width / 2,
        textY = height / 8;
      textAlign(CENTER, CENTER);
      const performance = Math.floor(
        bird.score / 10 >= 4 ? 4 : bird.score / 10
      );
      const endText = endingTexts[performance];
      for (let i = 0; i < endText.length; i++) {
        text(endText[i], textX, textY + i * h2LineSpace);
      }
      bird.show(isRestart);
    } else {
      if (openScreenBirdTracks.length > 40) {
        // captures a preview image for token
        hl.token.capturePreview();

        // render the tracks
        for (const track of openScreenBirdTracks) {
          track.show();
        }
        displayWelcome();
        return;
      }
      if ((!isRestart && !openScreenBird) || openScreenBird.isOutOfScreen()) {
        const direction = "northeast";
        const offsetL = (offset * width) / 13;
        offset++;

        let startingX = 0;
        let startingY = height;
        // randomly add offset to X or Y
        if (hl.randomElement([true, false])) {
          startingX = (offsetL + startingX) % width;
        } else {
          startingY = (offsetL + startingY) % height;
        }

        openScreenBird = new Bird(
          startingX,
          startingY,
          true,
          hl.random(15, 20),
          direction
        );
      }
      for (const track of openScreenBirdTracks) {
        track.show();
      }
      openScreenBird.move();
      openScreenBird.show();
      displayWelcome();
    }
    return;
  }
  if (frameCount % 40 === 0) {
    pipes.push(new Pipe());
    playing = true;
  }

  for (let i = pipes.length - 1; i >= 0; i--) {
    pipes[i].show();
    pipes[i].update();
    if (!pipes[i].isPass && pipes[i].offBird() && pipes[i].pass(bird)) {
      bird.score++;
      pipes[i].isPass = true;
    }
    if (pipes[i].offScreen()) {
      pipes.splice(i, 1);
    }
    if (pipes[i].hit(bird)) {
      playing = false;
      isRestart = true;
    }
  }
  // draw bird
  bird.show();
  bird.update();
  // show the current score
  if (playing) {
    fill(216, 223, 229);
    rectMode(CENTER);
    noStroke();
    rect(width / 6, height / 5, width / 6, width / 18);
    textSize(h3);
    fill(0);
    textAlign(CENTER, CENTER);
    text("Score: " + bird.score, width / 6, height / 5);
    image(backgroundImg, 0, -height / 19.8, width, height + height / 9.9);
  }
}

function displayWelcome() {
  openScreenStaticBird.show();
  push();
  textSize(h1);
  fill(0); // black color for text
  textAlign(CENTER);
  text("Welcome to Birdy!", width / 2, height / 4);
  pop();
  displayPropertyWithStar("Jump", midStart, 0, jump);
  displayPropertyWithStar("Body Weight", midStart, 1, bodyweight);
  displayPropertyWithStar("Difficulty", midStart, 2, difficulty);
}

function displayPropertyWithStar(property, start, level, startCount) {
  push();
  fill(0);

  text(`${property}:`, leftStart, start + h2LineSpace * 1.8 * (level || 0));
  // render starImg
  for (let i = 0; i < startCount; i++) {
    image(
      starImg,
      width / 2 + (i * width) / 12,
      start - h2LineSpace + h2LineSpace * 2 * (level || 0),
      h2 * 1.2,
      h2 * 1.2
    );
  }
  pop();
}

/*
 * Window resize
 */
function windowResized() {
  canvasWidth = Math.min(windowWidth, windowHeight);
  resizeCanvas(canvasWidth, canvasWidth);
  initialProperties();
  openScreenBirdTracks.length = 0;
}

/*
 * Keyboard shortcuts for saving, redrawing, etc.
 */
function keyTyped() {
  switch (key) {
    case "s":
      saveCanvas();
      break;
    case "r":
      redraw();
      break;
  }
}
